//Functions and constants
function copyAllMetadata(items, assets){
    var newItems = [];
    items.forEach(item => {
        item.check_flag = 1;
        //node.warn(assets[item.SKU_original]);
        if(assets[item.SKU_original]){
            item.validated = 1;
            newItems.push(copyMetadata(item, assets[item.SKU_original]));

        }
        else{
            item.validated = 0;
            newItems.push(item);
        }
    });

    return newItems;
}

function copyMetadata(item, asset, dataMap){
    dataMap = dataMap || defaultMapping;
    var newItem = item;
    dataMap.forEach(data => {
        if(data.copyFlag)
            newItem[data.item] = asset[data.asset] || newItem[data.item];
    });
    return newItem;
}

