var results = msg.payload;
let sqlRow = [];

var counterg = Math.ceil(results.length / 50);
global.set("counter", counterg);
global.set("toprocess", results.length);
global.set("tmp", 0);

var countersql = Math.ceil(results.length / 1000);
global.set("counter2", countersql);
global.set("toprocess2", results.length);
global.set("tmp2", 0);

var currentTime = new Date().getTime();
var toUpdate = [];
var failed = [];

let query = "";
let sqlHeader = "";
let sqlValueI = "";

const sqlCol = flow.get("MasterSQLCol");
const csvColumns = flow.get("MasterCSVCol");
results.forEach((row, i) => {
  csvColumns.forEach((csvCol, i) => {
      const regex = /\'/ig;
      if(csvCol !== 'Country' && csvCol !== 'Currency') {
           row[csvCol] = row[csvCol].toString().replace(regex, '\'\'');
      }
    switch (csvCol) {
      case "product_number":
        sqlRow[sqlCol.indexOf("ID")] = row[csvCol];
        sqlRow[sqlCol.indexOf("UniqueID")] = row[csvCol];
        break;
      case "department":
        sqlRow[sqlCol.indexOf("DepartmentID")] = row[csvCol];
        break;
      case "item_group":
        sqlRow[sqlCol.indexOf("GroupCode")] = row[csvCol];
        break;
      case "Country":
        sqlRow[sqlCol.indexOf("Country")] = "UAE";
        break;
      case "supplier":
        sqlRow[sqlCol.indexOf("SupplierNum")] = row[csvCol];
        break;
      case "supplier_name":
        sqlRow[sqlCol.indexOf("SupplierName")] = row[csvCol];
        break;
      case "barcode":
        let barcode = row[csvCol];
        let barcodePadded = barcode.toString().padStart(14, "0");
        sqlRow[sqlCol.indexOf("GTIN")] = barcodePadded;
        sqlRow[sqlCol.indexOf("GTINOriginal")] = barcode;
        break;
      case "external_item_number":
        sqlRow[sqlCol.indexOf("SupplierItemNum")] = row[csvCol];
        break;
      case "vsp":
        sqlRow[sqlCol.indexOf("TotalPrice")] = row[csvCol];
        break;
      case "Currency":
        sqlRow[sqlCol.indexOf("Currency")] = "AED";
        break;
      case "sku":
        let sku = row[csvCol];
        let skuPadded = sku.toString().padStart(14, "0");
        sqlRow[sqlCol.indexOf("sku")] = skuPadded;
        sqlRow[sqlCol.indexOf("SKU_original")] = sku;
        break;
      case "class":
        sqlRow[sqlCol.indexOf("Category")] = row[csvCol];
        break;
      case "subdepartment":
        sqlRow[sqlCol.indexOf("SubdepartmentID")] = row[csvCol];
        break;
      case "subclass":
        sqlRow[sqlCol.indexOf("Subcategory")] = row[csvCol];
        break;
      default:
        sqlRow[sqlCol.indexOf(csvCol)] = row.hasOwnProperty(csvCol)
          ? row[csvCol]
          : "";

        break;
    }
  });

  sqlRow.forEach((col, i) => {
    sqlValueI = sqlValueI + col + "','";
    sqlHeader = sqlHeader + sqlCol[i] + "','";

  });

  sqlValueI = sqlValueI.slice(0, sqlValueI.length - 2);
  sqlHeader = sqlHeader.slice(0, sqlHeader.length - 2);
  query =
    "INSERT or REPLACE INTO MASTER" +
    "('" +
    sqlHeader +
    ")" +
    " VALUES " +
    "('" +
    sqlValueI +
    ");" +
    "\n" +
    query;

  sqlValueI = "";
  sqlHeader = "";
});

msg.topic = "BEGIN TRANSACTION;" + "\n" + query + "COMMIT;";

msg.payload = toUpdate;
msg.FailedToUpdate = failed;
return msg;

var counter = global.get('counter2');
var tmp = global.get('tmp2');

//node.warn("COUNTER: "+ counter);
//node.warn("INCREASED COUNTER: "+ tmp);

if (tmp<=counter){

