if (!headers.apikey || headers.apikey != 'AIzaSyA_V9KPig2FAH3gtwkjUA_1R4k7vRvHP6M'){
    node.warn("Unauthorized Goods In transaction, wrong credentials. ");
    msg.payload = {"response":"Unauthorized"};
    msg.statusCode = 401;
    return [msg,null];
}
else{
    var results = msg.payload;
    node.warn("Authorized Goods In "+results[0].udfs.Retail_ItemBatchID.value+" transaction for "+results.length+" items, proceeding... ");
    var validPayload = Array.isArray(results);
    var validatedFields = '';
    if(validPayload){
        for(var item=0; item < originalPayload.length; item++ ){
            validatedFields = validateProps(originalPayload[item]);
            if(validatedFields.length > 0){
                msg.payload = {"response":"bad request missing field value " + validatedFields.toLocaleString()};
                msg.statusCode = 400;
                return [msg, payloadToProcess ];
            }
        }

        msg.payload = {"response":"Authorized. Processing Goods In for "+results.length+ " items"};
        msg.statusCode = 200;
        payloadToProcess = {payload: originalPayload}
    } else{
        msg.payload = {"response":"Invalid Format." };
        msg.statusCode = 400;
    }
    return [msg, payloadToProcess ];
}

