//Functions and constants
function copyAllMetadata(items, assets){
    var newItems = [];
    items.forEach(item => {
        item.check_flag = 1;
        //node.warn(assets[item.TagID]);
        if(assets[item.serialNumber]){
            item.validated = 1;
            newItems.push(copyMetadata(item, assets[item.serialNumber]));

        }
        else{
            item.validated = 0;
            newItems.push(item);
        }
    });

    return newItems;
}

function copyMetadata(item, asset, dataMap){
    dataMap = dataMap || defaultMapping;
    var newItem = item;
    var value = "";
    dataMap.forEach(data => {
        if(data.copyFlag)
            if(asset[data.asset] !== "" || typeof asset[data.asset] !== "undefined"){
                value = asset[data.asset];
            }else{
                value="";
            }
            //newItem[data.item] = asset[data.asset] || newItem[data.item];
            newItem[data.item] = value;
    });
    return newItem;
}

