var oldMovedBinLocation = flow.get("movedLocation") || null; // Store the previous movedBinlocation in flow context

if (msg.payload.operationType === "update") {
    var updatedFields = msg.payload.updateDescription.updatedFields;

    if (
        "movedlocation" in updatedFields &&
        updatedFields.movedBinlocation !== oldMovedBinLocation &&
        (updatedFields.operation === "putAwaye" || updatedFields.operation === "full movement")
    ) {
        flow.set("movedLocation", updatedFields.movedBinlocation); // Update the stored movedBinlocation
        var alertMessage = `ASN is moved to ${updatedFields.movedBinlocation} movedLocation`;
        msg.payload = alertMessage;
